import React, { useState, useEffect } from 'react';
import MoviesCard from '../MoviesCard/MoviesCard.jsx';
import styles from './MoviesCardList.module.css';

function MoviesCardsList({ movies }) {
  const [visibleCards, setVisibleCards] = useState([]);
  const isMobile = window.innerWidth < 768;

  useEffect(() => {
    setVisibleCards(isMobile ? movies.slice(0, 5) : movies);
  }, [movies, isMobile]);

  const loadMore = () => {
    setVisibleCards(current => [
      ...current,
      ...movies.slice(current.length, current.length + (isMobile ? 5 : 3))
    ]);
  };

  return (
    <section className={styles["movies-list"]}>
      {visibleCards.map((movie, index) => (
        <MoviesCard key={index} movie={movie} />
      ))}
      {visibleCards.length < movies.length && (
        <button className={styles["movies-list__more-button"]} onClick={loadMore}>
          Ещё
        </button>
      )}
    </section>
  );
}

export default MoviesCardsList;
