// MoviesCardsList.jsx
import React from 'react';
import { useLocation } from 'react-router-dom';
import MoviesCard from '../MoviesCard/MoviesCard.jsx';
import styles from './MoviesCardList.module.css';

function MoviesCardsList({ onLike, onRemove, savedMovies, movies }) {
  const location = useLocation();
  const isSavedMoviesPage = location.pathname === '/saved-movies';
  const moviesToDisplay = isSavedMoviesPage ? savedMovies : movies; // moviesData представляет все фильмы

  return (
    <section className={styles["movies-list"]}>
      {moviesToDisplay.map((movie, index) => (
        <MoviesCard
          key={index}
          movieName={movie.movieName}
          movieImage={movie.movieImage}
          movieLink={movie.movieLink}
          onLike={() => onLike(movie)}
          onRemove={() => onRemove(movie)}
          isLiked={savedMovies.includes(movie)}
        />
      ))}
      {!isSavedMoviesPage && (
        <button
          className={styles["movies-list__more-button"]}
          type="button"
        >
          Ещё
        </button>
      )}
    </section>
  );
}

export default MoviesCardsList;
