import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { Link } from "react-router-dom";
import Navigation from "../Navigation/Navigation.jsx";
import NavTab from "../NavTab/NavTab.jsx";
import BurgerMenu from "./BurgerMenu/BurgerMenu.jsx";
import headerLogo from "../../images/logo__header.svg";
import styles from "./Header.module.css";

function Header() {
  const location = useLocation();
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [burgerOpen, setBurgerOpen] = useState(false);

  const loggedIn = ['/movies', '/profile', '/saved-movies'];
  const noHeaderPaths = ['/signin', '/signup', '/404'];

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleBurgerClick = () => {
    setBurgerOpen(!burgerOpen);
  };

  if (noHeaderPaths.includes(location.pathname)) return null;

  return (
    <header className={styles.header}>
      <div className={styles.header__container}>
        <Link to="/" className={styles.header__logo}>
          <img className={styles.logo_img} src={headerLogo} alt="Лого" />
        </Link>
        {isMobile && <BurgerMenu onClick={handleBurgerClick} isOpen={burgerOpen} />}
        {loggedIn.includes(location.pathname) && !isMobile && <Navigation />}
        {!isMobile && <NavTab />}
      </div>
    </header>
  );
}

export default Header;
