import React from 'react';

import Search from '../SearchForm/SearchForm.jsx';
import MoviesCardsList from '../MoviesCardList/MoviesCardList.jsx';
import styles from './SavedMovies.module.css';

function SavedMovies({ searchValue, searchChangeValueHandler, searchHandler, formValidation, searchedMovies, removeMovie, isLoading }) {
  return (
    <main className={styles["saved-movies"]}>
      <Search
        searchValue={searchValue}
        searchChangeValueHandler={searchChangeValueHandler}
        searchHandler={searchHandler}
        formValidation={formValidation}
      />
      {searchedMovies.length === 0 ? (
        <p className={styles["moviesListNotFound"]}>Ничего не найдено</p>
      ) : (
        <MoviesCardsList
          searchedMovies={searchedMovies}
          searchValue={searchValue}
          removeMovie={removeMovie}
          isLoading={isLoading}
        />
      )}
    </main>
  );
}

export default SavedMovies;
