import React, { useState } from 'react';
import SearchForm from '../SearchForm/SearchForm.jsx';
import MoviesCardsList from '../MoviesCardList/MoviesCardList.jsx';
import styles from './Movies.module.css'; 

function Movies() {
  // Предположим, у нас есть массив с фильмами, которые пользователь уже лайкнул
  const [likedMovies, setLikedMovies] = useState([]);

  // Функция для добавления лайка на фильм
  const handleLike = (movie) => {
    setLikedMovies([...likedMovies, movie]);
  };

  // Функция для удаления лайка с фильма
  const handleRemoveLike = (movie) => {
    setLikedMovies(likedMovies.filter(m => m.movieName !== movie.movieName));
  };

  return (
    <main className={styles["movies"]}>
      <SearchForm />
      <MoviesCardsList
        onLike={handleLike}
        onRemove={handleRemoveLike}
      />
    </main>
  );
}

export default Movies;
