import React, { useState }  from 'react';
import styles from './MoviesCard.module.css'; 

function MoviesCard({ movie, onLike, onRemove }) {
  const [isLiked, setIsLiked] = useState(false);

  const handleLikeClick = () => {
    setIsLiked(!isLiked); // Переключаем состояние лайка

    // Здесь может быть дополнительная логика для обновления списка сохраненных фильмов или отправки данных на сервер
    // Например:
    if (!isLiked) {
      // Добавляем фильм в список сохраненных, если он был не лайкнут
      onLike(movie);
    } else {
      // Удаляем фильм из списка сохраненных, если он был лайкнут
      onRemove(movie);
    }
  };





  return (
    <article className={styles["movies-card"]}>
      <div className={styles["movies-card__cover"]}>
        <a href={movie.movieLink} target="_blank" rel="noreferrer">
          <img
            className={styles["movies-card__image"]}
            src={movie.movieImage}
            alt={movie.movieName}
          />
        </a>
      </div>
      <div className={styles["movies-card__footer"]}>
        <div className={styles["movies-card__data"]}>
          <h2 className={styles["movies-card__name"]}>{movie.movieName}</h2>
          <button
        className={isLiked ? styles['movies-card__like-button_active'] : styles['movies-card__like-button']}
        type="button"
        aria-label={isLiked ? 'Удалить из сохраненных' : 'Сохранить'}
        onClick={handleLikeClick}
      />
        </div>
        <div className={styles["movies-card__meta"]}>
          <span className={styles["movie__duration"]}>1ч 42м</span>
        </div>
      </div>
    </article>
  );
}

export default MoviesCard;
