import React, { useState } from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';

// Импортируем компоненты
import Main from '../Main/Main.jsx';
import Header from '../Header/Header.jsx';
import Footer from '../Footer/Footer.jsx';
import Movies from '../Movies/Movies.jsx';
import SavedMovies from '../SavedMovies/SavedMovies.jsx';
import Login from '../Login/Login.jsx';
import Register from '../Register/Register.jsx';
import NotFound from '../NotFound/NotFound.jsx';
import UserEdit from '../UserEdit/UserEdit.jsx';

import { moviesData } from '../../utils/moviesData.js'; //массив фильмов

function App() {
  const [movies, setMovies] = useState(moviesData); // все фильмы
  const [savedMovies, setSavedMovies] = useState([]); // лайкнутые фильмы

  useEffect(() => {
    // Загрузка сохраненных фильмов из localStorage при монтировании компонента
    const savedMoviesData = localStorage.getItem('savedMovies');
    if (savedMoviesData) {
      setSavedMovies(JSON.parse(savedMoviesData));
    }
  }, []);

  const handleLike = (likedMovie) => {
    const updatedMovies = movies.map(movie => 
      movie.movieName === likedMovie.movieName ? { ...movie, isLiked: !movie.isLiked } : movie
    );
    setMovies(updatedMovies);

    const newSavedMovies = updatedMovies.filter(movie => movie.isLiked);
    setSavedMovies(newSavedMovies);

    // Сохранение измененного списка фильмов в localStorage
    localStorage.setItem('savedMovies', JSON.stringify(newSavedMovies));
  };

  const handleRemove = (removedMovie) => {
    const updatedSavedMovies = savedMovies.filter(movie => movie.movieName !== removedMovie.movieName);
    setSavedMovies(updatedSavedMovies);

    // Обновление лайков в общем списке фильмов
    const updatedMovies = movies.map(movie => 
      movie.movieName === removedMovie.movieName ? { ...movie, isLiked: false } : movie
    );
    setMovies(updatedMovies);

    // Обновление localStorage
    localStorage.setItem('savedMovies', JSON.stringify(updatedMovies.filter(m => m.isLiked)));
  };

  return (
    <div className="app">
      <Header />
      <Routes>
        <Route path="signup" element={<Register />} />
        <Route path="signin" element={<Login />} />
        <Route path="/" element={<Main />} />
        <Route path="profile" element={<UserEdit />} />
        <Route 
          path="movies" 
          element={
            <Movies 
              movies={movies} 
              onLike={handleLike}
              onRemove={handleRemove}
            />
          } 
        />
        <Route 
          path="saved-movies" 
          element={
            <SavedMovies 
              savedMovies={savedMovies} 
              onLike={handleLike}
              onRemove={handleRemove}
            />
          } 
        />
        <Route path="/404" element={<NotFound />} />
        <Route path="*" element={<Navigate to="/404" replace />} />
      </Routes>
      <Footer />
    </div>
  );
}

export default App;
