import React, { useState } from "react";
import styles from "./BurgerMenu.module.css";

function Burger() {
  const [isOpen, setIsOpen] = useState(false);

  const handleClick = () => {
    setIsOpen(!isOpen); // Переключаем состояние
  };

  const activeClass = isOpen ? styles.burger_active : '';

  return (
    <div className={`${styles.burger} ${activeClass}`} onClick={handleClick}>
      <span className={styles.burger__item} />
      <span className={styles.burger__item} />
      <span className={styles.burger__item} />
    </div>
  );
}

export default Burger;
