import React from 'react';
import { NavLink } from 'react-router-dom';
import ProfileIcon from '../../images/profile.svg';
import styles from './Navigation.module.css';

function Navigation({ isOpen }) {
  const getNavLinkClass = ({ isActive }) => 
    isActive ? `${styles['nav__link']} ${styles['active']}` : styles['nav__link'];

  const navClass = isOpen ? `${styles['nav']} ${styles['nav_open']}` : styles['nav'];

  return (
    <nav className={navClass}>

      <ul className={styles['nav__list']}>
      <NavLink
        className={getNavLinkClass}
        to="/"
        end
        aria-label="На главную"
      >
        Главная
      </NavLink>
        <li className={styles['nav__item']}>
          <NavLink
            className={getNavLinkClass}
            to="/movies"
            aria-label="Фильмы"
          >
            Фильмы
          </NavLink>
        </li>
        <li className={styles['nav__item']}>
          <NavLink
            className={getNavLinkClass}
            to="/saved-movies"
            aria-label="Сохранённые фильмы"
          >
            Сохранённые фильмы
          </NavLink>
        </li>
      </ul>
      <NavLink
        to="/profile"
        className={getNavLinkClass}
        aria-label="Аккаунт"
      >
        <img className={styles['nav_icon']} src={ProfileIcon} alt="профиль" />
      </NavLink>
    </nav>
  );
}

export default Navigation;
