import React, { useState } from 'react';
import styles from './MoviesCard.module.css'; 

function MoviesCard({ movie, onLike, onRemove, isSavedPage }) {
  const [isLiked, setIsLiked] = useState(false);

  const handleLikeClick = () => {
    // Только если это не страница сохранённых фильмов
    if (!isSavedPage) {
      setIsLiked(!isLiked);
      onLike(movie);
    }
  };

  const handleRemoveClick = () => {
    // Для страницы сохранённых фильмов
    if (isSavedPage) {
      onRemove(movie);
    }
  };

  return (
    <article className={styles["movies-card"]}>
      <div className={styles["movies-card__cover"]}>
        <a href={movie.movieLink} target="_blank" rel="noreferrer">
          <img
            className={styles["movies-card__image"]}
            src={movie.movieImage}
            alt={movie.movieName}
          />
        </a>
      </div>
      <div className={styles["movies-card__footer"]}>
        <div className={styles["movies-card__data"]}>
          <h2 className={styles["movies-card__name"]}>{movie.movieName}</h2>
          <button
            className={isSavedPage ? styles["button-card-delete"] : (isLiked ? styles["button-card-like_active"] : styles["button-card-like"])}
            type="button"
            aria-label={isSavedPage ? "удалить фильм" : "сохранить фильм"}
            onMouseEnter={isSavedPage ? handleRemoveClick : null}
            onMouseLeave={isSavedPage ? handleLikeClick : null}
            onClick={isSavedPage ? handleRemoveClick : handleLikeClick}
          />
        </div>
        <div className={styles["movies-card__meta"]}>
          <span className={styles["movie__duration"]}>1ч 42м</span>
        </div>
      </div>
    </article>
  );
}

export default MoviesCard;
