import React, { useContext, useEffect } from 'react';
import CurrentUserContext from '../../Contexts/CurrentUserContext';
import AppContext from '../../Contexts/AppContext';

import Profile from '../../Profile/profile';
import Input from '../../Input/input';


function UserEdit({ onUpdateUser, handleLogout, name, email, onNameChange, onEmailChange }) {
  const currentUser = useContext(CurrentUserContext);
  const { setIsLoading } = useContext(AppContext);

  useEffect(() => {
    onNameChange(currentUser.name);
    onEmailChange(currentUser.email);
  }, [currentUser, onNameChange, onEmailChange]);

  function handleSubmit(e) {
    e.preventDefault();
    setIsLoading(true);
    onUpdateUser(name, email);
  }

  return (
    <Profile
      formName="useredit"
      title={name}
      SubmitBtnText="Редактировать"
      ExitBtnText="Выйти из аккаунта"
      handleLogout={handleLogout}
      onSubmit={handleSubmit}
    >
      <Input
        title="Имя"
        name="name"
        type="text"
        value={name}
        onChange={onNameChange}
        required
      />
      <Input
        title="E-mail"
        name="email"
        type="email"
        value={email}
        onChange={onEmailChange}
        required
      />
    </Profile>
  );
}

export default UserEdit;
