function Header({handleBurgerClose, handleClickBurger}) {
  const location = useLocation();
  // const [isBurgerMenuOpen, setIsBurgerMenuOpen] = useState(false);

  const loggedIn = ['/movies', '/profile', '/saved-movies'];
  const noHeaderPaths = ['/signin', '/signup', '/404'];

  // const handleBurgerClick = () => {
  //   setIsBurgerMenuOpen(!isBurgerMenuOpen);
  // };

  if (noHeaderPaths.includes(location.pathname)) return null;

  return (
    <header className={styles.header}>
      <div className={styles.header__container}>
        <Link to="/" className={styles.header__logo}>
          <img src={headerLogo} alt="Лого" />
        </Link>
        {loggedIn.includes(location.pathname) && (
          <>
            <Navigation />
            <BurgerMenu onClick={handleBurgerClose} isOpen={handleClickBurger} />
          </>
        )}
        {!loggedIn.includes(location.pathname) && <NavTab />}
      </div>
    </header>
  );
}

export default Header;
