import React from 'react';
import { NavLink } from 'react-router-dom';
import styles from './Navigation.module.css';
import ToProfileIcon from '../../images/to-profile-icon.svg';

function Navigation({ isOpen }) {
  const navClass = isOpen ? `${styles.nav} ${styles.nav_open}` : styles.nav;

  const activeClassName = ({ isActive }) => 
    `${styles.nav__link} ${isActive ? styles.active : ''}`;

  return (
    <nav className={navClass}>
      <ul className={styles.nav__list}>
        <li className={`${styles.nav__item} ${styles.nav__item_invisible}`}>
          <NavLink
            className={activeClassName}
            to="/"
            end
            aria-label="На главную"
          >
            Главная
          </NavLink>
        </li>
        <li className={styles.nav__item}>
          <NavLink
            className={activeClassName}
            to="/movies"
            aria-label="Фильмы"
          >
            Фильмы
          </NavLink>
        </li>
        <li className={styles.nav__item}>
          <NavLink
            className={activeClassName}
            to="/saved-movies"
            aria-label="Сохранённые фильмы"
          >
            Сохранённые фильмы
          </NavLink>
        </li>
      </ul>
      <NavLink
        to="/profile"
        className={({ isActive }) => 
          `${styles.nav__link} ${styles.nav__profile} ${isActive ? styles.active : ''}`}
        aria-label="Аккаунт"
      >
        Аккаунт
        <img className={styles.nav__icon} src={ToProfileIcon} alt="профиль" />
      </NavLink>
    </nav>
  );
}

export default Navigation;
