import React from 'react';
import { useLocation } from 'react-router-dom';

import styles from './input.module.css';

function Input(props) {
  const { pathname } = useLocation();

  // Деструктурирование объекта пропсов
  const {
    addClassName,
    title,
    className,
    errorMessage,
    name,
    type,
    minLength,
    maxLenght,
    value,
    onChange,
    pattern,
    onBlur,
  } = props;

  return (
    <div className={styles[`input__container ${addClassName}`]}>
      <span className={styles["input__title"]}>{title}</span>
      <input
        className={styles[`${className} ${errorMessage ? 'input_error' : ''}`]}
        name={name}
        type={type}
        required
        minLength={minLength}
        maxLength={maxLenght}
        value={value || ''}
        onChange={onChange}
        pattern={pattern}
        onBlur={onBlur}
      />
      <p
       className={pathname === '/profile' 
       ? `${styles['input-error']} ${styles['input-error__profile']}` 
       : styles['input-error']}
      >
        {errorMessage}
      </p>
    </div>
  );
}

export default Input;
