import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import ProfileIcon from '../../images/profile.svg';
import styles from './Navigation.module.css'; 

function Navigation() {
  const [isOpen, setIsOpen] = useState(false);
  const toggleMenu = () => setIsOpen(!isOpen);

  const activeClassName = ({ isActive }) => 
    `${styles['nav__link']} ${isActive ? styles['nav__item_invisible'] : ''}`;

  return (
    <nav className={`${styles['nav']} ${isOpen ? styles['nav_open'] : ''}`}>
      <div className={styles['nav__toggle']} onClick={toggleMenu}>
        {/* Icon for menu toggle */}
      </div>
      <ul className={styles['nav__list']}>
        <li className={styles['nav__item']}>
          <NavLink
            className={activeClassName}
            to="movies"
            aria-label="Фильмы"
          >
            Фильмы
          </NavLink>
        </li>
        <li className={styles['nav__item']}>
          <NavLink
            className={activeClassName}
            to="saved-movies"
            aria-label="Сохранённые фильмы"
          >
            Сохранённые фильмы
          </NavLink>
        </li>
      </ul>
      <NavLink
        to="profile"
        className={({ isActive }) => 
          `${styles['nav__link']} ${styles['nav__profile']} ${isActive ? styles['active'] : ''}`}
        aria-label="Аккаунт"
      >
        <img className={styles['nav_icon']} src={ProfileIcon} alt="профиль" />
      </NavLink>
    </nav>
  );
}

export default Navigation;
