import React, { useContext } from 'react';
import { NavLink } from 'react-router-dom';
import {AppContext} from '../../Context/AppContext';
import ProfileIcon from '../../images/profile.svg';

import styles from './Navigation.module.css'; 

function Navigation({ onBurgerClose }) {
  const { stateMenu } = useContext(AppContext);
  const menuOpen = stateMenu ? styles.navOpen : '';

  function handleClickLink(e) {
    if (e.currentTarget.className.includes('active')) {
      return;
    }
    onBurgerClose();
  }

  const activeClassName = ({ isActive }) => `${styles.navLink} ${isActive ? styles.active : ''}`;

  return (
    <nav className={`${styles.nav} ${menuOpen}`}>
      <ul className={styles.navList}>
        <li className={`${styles.navItem} ${styles.navItemInvisible}`}>
          <NavLink
            className={activeClassName}
            to="/"
            end
            aria-label="На главную"
            onClick={handleClickLink}
          >
            Главная
          </NavLink>
        </li>
        <li className={styles.navItem}>
          <NavLink
            className={activeClassName}
            to="movies"
            aria-label="Фильмы"
            onClick={handleClickLink}
          >
            Фильмы
          </NavLink>
        </li>
        <li className={styles.navItem}>
          <NavLink
            className={activeClassName}
            to="saved-movies"
            aria-label="Сохранённые фильмы"
            onClick={handleClickLink}
          >
            Сохранённые фильмы
          </NavLink>
        </li>
      </ul>
      <NavLink
        to="profile"
        className={({ isActive }) => `${styles.navLink} ${styles.navProfile} ${isActive ? styles.active : ''}`}
        aria-label="Аккаунт"
        onClick={handleClickLink}
      >
        Аккаунт
        <img className={styles.navIcon} src={ProfileIcon} alt="профиль" />
      </NavLink>
    </nav>
  );
}

export default Navigation;
