import React, { useContext } from 'react';
import {AppContext} from '../../Context/AppContext';
import Preloader from '../../vendor/Preloader/Preloader';
import styles from './Profile.module.css'; // Импорт CSS-модулей

function Profile({ title, formName, onSubmit, SubmitBtnText, ExitBtnText, handleLogout, children }) {
  const { isLoading } = useContext(AppContext);

  return (
    <div className={styles["profile"]}>
      <h1 className={styles["profile__title"]}>
        Привет,&nbsp;{title}!
      </h1>
      {isLoading ? <Preloader /> : (
        <form
          className={styles["profile__form"]}
          name={formName}
          onSubmit={onSubmit}
          noValidate
        >
          {children}
          <button
            className={`${styles["profile__button"]} ${styles["profile__button-submit"]}`}
            type="submit"
            aria-label="Редактировать данные"
          >
            {SubmitBtnText}
          </button>
          <button
            className={`${styles["profile__button"]} ${styles["profile__button-exit"]}`}
            type="button"
            aria-label="Выйти из аккаунта"
            onClick={handleLogout}
            disabled={!!isLoading}
          >
            {ExitBtnText}
          </button>
        </form>
      )}
    </div>
  );
}

export default Profile;
