import React from 'react';
import { useLocation } from 'react-router-dom';
import MoviesCard from '../MoviesCard/MoviesCard.jsx';
import styles from './MoviesCardList.module.css';

import oneFilm from '../../images/card-film__img/one_film.svg';
import twoFilm from '../../images/card-film__img/two_film.svg';
import threeFilm from '../../images/card-film__img/three_film.svg';
import fourFilm from '../../images/card-film__img/four_film.svg';
import fiveFilm from '../../images/card-film__img/five_film.svg';
import sixFilm from '../../images/card-film__img/six_film.svg';
import sevenFilm from '../../images/card-film__img/seven_film.svg';
import eightFilm from '../../images/card-film__img/eight_film.svg';
import nineFilm from '../../images/card-film__img/nine_film.svg';
import tenFilm from '../../images/card-film__img/ten_film.svg';
import elevenFilm from '../../images/card-film__img/eleven_film.svg';
import twelveFilm from '../../images/card-film__img/twelve_film.svg';
import thirteenFilm from '../../images/card-film__img/thirteen_film.svg';
import fourteenFilm from '../../images/card-film__img/fourteen_film.svg';
import fifteenFilm from '../../images/card-film__img/fifteen_film.svg';
import sixteenFilm from '../../images/card-film__img/sixteen_film.svg';

function MoviesCardsList({ onLike, onRemove }) {
  const location = useLocation();
  const isSavedMoviesPage = location.pathname === '/saved-movies';

  const movies = [
    { movieName: '33 слова о дизайне', movieImage: {oneFilm}, movieLink: '#' },
    { movieName: 'Киноальманах «100 лет дизайна»', movieImage: twoFilm, movieLink: '#' },
    { movieName: 'В погоне за Бенкси', movieImage: threeFilm, movieLink: '#' },
    { movieName: 'Баския: Взрыв реальности', movieImage: fourFilm, movieLink: '#' },
    { movieName: 'Бег это свобода', movieImage: fiveFilm, movieLink: '#' },
    { movieName: 'Книготорговцы', movieImage: sixFilm, movieLink: '#' },
    { movieName: 'Когда я думаю о Германии ночью', movieImage: sevenFilm, movieLink: '#' },
    { movieName: 'Gimme Danger: История Игги и The Stooges', movieImage: eightFilm, movieLink: '#' },
    { movieName: 'Дженис: Маленькая девочка грустит', movieImage: nineFilm, movieLink: '#' },
    { movieName: 'Соберись перед прыжком', movieImage: tenFilm, movieLink: '#' },
    { movieName: 'Пи Джей Харви: A dog called money', movieImage: elevenFilm, movieLink: '#' },
    { movieName: 'По волнам: Искусство звука в кино', movieImage: twelveFilm, movieLink: '#' },
    { movieName: 'Рудбой', movieImage: thirteenFilm, movieLink: '#' },
    { movieName: 'Скейт — кухня', movieImage: fourteenFilm, movieLink: '#' },
    { movieName: 'Война искусств', movieImage: fifteenFilm, movieLink: '#' },
    { movieName: 'Зона', movieImage: sixteenFilm, movieLink: '#' }
  ];

  return (
    <section className={styles["movies-list"]}>
      {movies.map((movie, index) => (
        <MoviesCard
          key={index}
          movieName={movies.movieName}
          movieImage={movie.movieImage}
          movieLink={movie.movieLink}
          onLike={onLike}
          onRemove={onRemove}
          isLiked={isSavedMoviesPage}
          movie={movies}
        />
      ))}
      {!isSavedMoviesPage && (
        <button
          className={styles["movies-list__more-button"]}
          type="button"
        >
          Ещё
        </button>
      )}
    </section>
  );
}

export default MoviesCardsList;
