import React from "react";
import styles from "./BurgerMenu.module.css";

function Burger({ handleClickBurger }) {
  // const [isOpen, setIsOpen] = useState(false);

  // const handleClick = () => {
  //   setIsOpen(!isOpen); // Переключаем состояние
  // };

  const activeClass =  handleClickBurger ? styles.burger_active : '';

  return (
    <div className={`${styles.burger} ${activeClass}`} onClick={handleClickBurger}>
      <span className={styles.burger__item} />
      <span className={styles.burger__item} />
      <span className={styles.burger__item} />
    </div>
  );
}

export default Burger;
