import React from 'react';
import styles from './MoviesCard.module.css'; 

function MoviesCard({ movieName, movieImage, movieLink, isLiked, onLike, onRemove, isSavedMoviesPage }) {
  // const [isLiked, setIsLiked] = useState(false);
  const buttonClassName = isLiked ? styles["button-card-delete"] : styles["button-card-like"];
  const handleButtonClick = () => {
    isLiked ? onRemove() : onLike();
  };

  // const handleRemoveClick = () => {
  //   // Для страницы сохранённых фильмов
  //   if (props.isSavedPage) {
  //     props.onRemove(props.movie);
  //   }
  // };

  return (
    <article className={styles["movies-card"]}>
      <div className={styles["movies-card__cover"]}>
        <a href={movieLink} target="_blank" rel="noreferrer">
          <img
            className={styles["movies-card__image"]}
            src={movieImage}
            alt={movieName}
          />
        </a>
      </div>
      <div className={styles["movies-card__footer"]}>
        <div className={styles["movies-card__data"]}>
          <h2 className={styles["movies-card__name"]}>{movieName}</h2>
          <button
        className={buttonClassName}
        type="button"
        aria-label={isLiked ? "Удалить фильм" : "Сохранить фильм"}
        onMouseEnter={isSavedMoviesPage ? handleButtonClick : undefined}
        onMouseLeave={isSavedMoviesPage ? handleButtonClick : undefined}
      />
        </div>
        <div className={styles["movies-card__meta"]}>
          <span className={styles["movie__duration"]}>1ч 42м</span>
        </div>
      </div>
    </article>
  );
}

export default MoviesCard;
