import React, { useState }  from 'react';
import styles from './MoviesCard.module.css'; 

function MoviesCard({ movie, onLike, onRemove, isLikedInitially, movieLink, movieImage, movieName  }) {
  const [isLiked, setIsLiked] = useState(isLikedInitially);

  const handleLikeClick = () => {
    setIsLiked(!isLiked);
    onLike(movie);
  };

  const handleRemoveClick = () => {
    setIsLiked(false); // Если удаляем фильм, то снимаем лайк
    onRemove(movie);
  };



  return (
    <article className={styles["movies-card"]}>
      <div className={styles["movies-card__cover"]}>
        <a href={movieLink} target="_blank" rel="noreferrer">
          <img
            className={styles["movies-card__image"]}
            src={movieImage}
            alt={movieName}
          />
        </a>
      </div>
      <div className={styles["movies-card__footer"]}>
        <div className={styles["movies-card__data"]}>
          <h2 className={styles["movies-card__name"]}>{movieName}</h2>
          <button
        className={isLiked ? styles["button-card-like_active"] : styles["button-card-like"]}
        onClick={isLikedInitially ? handleRemoveClick : handleLikeClick}
        type="button"
        aria-label={isLiked ? "Удалить фильм" : "Поставить лайк"}
      />
        </div>
        <div className={styles["movies-card__meta"]}>
          <span className={styles["movie__duration"]}>1ч 42м</span>
        </div>
      </div>
    </article>
  );
}

export default MoviesCard;
