import React, { useState } from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';

// Импортируем компоненты
import Main from '../Main/Main.jsx';
import Header from '../Header/Header.jsx';
import Footer from '../Footer/Footer.jsx';
import Movies from '../Movies/Movies.jsx';
import SavedMovies from '../SavedMovies/SavedMovies.jsx';
import Login from '../Login/Login.jsx';
import Register from '../Register/Register.jsx';
import NotFound from '../NotFound/NotFound.jsx';
import UserEdit from '../UserEdit/UserEdit.jsx';

import { moviesData } from '../Movies/moviesData'; // предполагается, что moviesData - это массив всех фильмов

function App() {
  const [movies, setMovies] = useState(moviesData); // все фильмы
  const [savedMovies, setSavedMovies] = useState([]); // лайкнутые фильмы

  // Обработка лайка фильма
  const handleLike = (movie) => {
    const updatedMovies = movies.map(m => 
      m.movieName === movie.movieName ? { ...m, isLiked: !m.isLiked } : m
    );
    setMovies(updatedMovies);

    const newSavedMovies = updatedMovies.filter(m => m.isLiked);
    setSavedMovies(newSavedMovies);
  };

  // Обработка удаления фильма из сохраненных
  const handleRemove = (movie) => {
    const updatedSavedMovies = savedMovies.filter(m => m.movieName !== movie.movieName);
    setSavedMovies(updatedSavedMovies);
  };

  return (
    <div className="app">
      <Header />
      <Routes>
        <Route path="signup" element={<Register />} />
        <Route path="signin" element={<Login />} />
        <Route path="/" element={<Main />} />
        <Route path="profile" element={<UserEdit />} />
        <Route 
          path="movies" 
          element={
            <Movies 
              movies={movies} 
              onLike={handleLike}
            />
          } 
        />
        <Route 
          path="saved-movies" 
          element={
            <SavedMovies 
              savedMovies={savedMovies} 
              onRemove={handleRemove}
            />
          } 
        />
        <Route path="/404" element={<NotFound />} />
        <Route path="*" element={<Navigate to="/404" replace />} />
      </Routes>
      <Footer />
    </div>
  );
}

export default App;
