import React, { useState,useEffect } from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';

// Импортируем компоненты
import Main from '../Main/Main.jsx';
import Header from '../Header/Header.jsx';
import Footer from '../Footer/Footer.jsx';
import Movies from '../Movies/Movies.jsx';
import SavedMovies from '../SavedMovies/SavedMovies.jsx';
import Login from '../Login/Login.jsx';
import Register from '../Register/Register.jsx';
import NotFound from '../NotFound/NotFound.jsx';
import UserEdit from '../UserEdit/UserEdit.jsx';

import { moviesData } from '../../utils/moviesData.js'; //массив фильмов

function App() {
  const [movies, setMovies] = useState(moviesData); // все фильмы
  const [savedMovies, setSavedMovies] = useState([]); // лайкнутые фильмы


  useEffect(() => {
    // Загрузка всех фильмов и сохраненных фильмов из localStorage
    const loadedMovies = JSON.parse(localStorage.getItem('movies')) || moviesData;
    const loadedSavedMovies = JSON.parse(localStorage.getItem('savedMovies')) || [];

    setMovies(loadedMovies);
    setSavedMovies(loadedSavedMovies);
  }, []);

  const handleLike = (movie) => {
    const isLiked = savedMovies.some(savedMovie => savedMovie.movieName === movie.movieName);
    const updatedSavedMovies = isLiked 
      ? savedMovies.filter(savedMovie => savedMovie.movieName !== movie.movieName)
      : [...savedMovies, movie];

    setSavedMovies(updatedSavedMovies);
    localStorage.setItem('savedMovies', JSON.stringify(updatedSavedMovies));
  };

  const handleRemove = (movie) => {
    const updatedSavedMovies = savedMovies.filter(savedMovie => savedMovie.movieName !== movie.movieName);
    setSavedMovies(updatedSavedMovies);
    localStorage.setItem('savedMovies', JSON.stringify(updatedSavedMovies));
  };


  return (
    <div className="app">
      <Header />
      <Routes>
        <Route path="signup" element={<Register />} />
        <Route path="signin" element={<Login />} />
        <Route path="/" element={<Main />} />
        <Route path="profile" element={<UserEdit />} />
        <Route 
          path="movies" 
          element={
            <Movies 
              movies={movies} 
              onLike={handleLike}
              onRemove={handleRemove}
            />
          } 
        />
        <Route 
          path="saved-movies" 
          element={
            <SavedMovies 
              savedMovies={savedMovies} 
              onLike={handleLike}
              onRemove={handleRemove}
            />
          } 
        />
        <Route path="/404" element={<NotFound />} />
        <Route path="*" element={<Navigate to="/404" replace />} />
      </Routes>
      <Footer />
    </div>
  );
}

export default App;
