import React from 'react';

import SearchForm from './SearchForm/searchform';
import MoviesCardsList from './MoviesCardsList/moviescardslist';

import styles from './movies.module.css'; // Обновленный импорт стилей

function Movies({
  searchValue,
  searchChangeValueHandler,
  searchHandler,
  searchedMovies,
  likeMovie,
  removeMovie,
  isLoading
}) {
  return (
    <main className={styles["movies"]}>
      <SearchForm
        searchValue={searchValue}
        searchChangeValueHandler={searchChangeValueHandler}
        searchHandler={searchHandler}
      />
      {searchedMovies ? (
        <MoviesCardsList
          searchedMovies={searchedMovies}
          searchValue={searchValue}
          likeMovie={likeMovie}
          removeMovie={removeMovie}
          isLoading={isLoading}
        />
      ) : ('')}
    </main>
  );
}

export default Movies;
