import React from 'react';

import { Link } from 'react-router-dom';
import HeaderLogo from '../../images/logo__header.svg';

function AuthForm({ title, formName, onSubmit, valid, btnText, children }) {
  return (
    <div className={"auth-form"}> 
      <Link className={"auth-form__link"} to="/"> 
        <img className={"logoImg"} src={HeaderLogo} alt="Лого" /> 
      </Link>
      <h1 className={"auth-form__title"}>{title}</h1> 
      <form
        className={"auth-form__form"} 
        name={formName}
        onSubmit={onSubmit}
        noValidate
      >
        {children}
        <button 
          className={valid === false ? "auth-form__submit-button_invalid" : "authForm__submit-button"}  
          type="submit"
        >
          {btnText}
        </button>
      </form>
      {formName === 'registration' ? (
        <p className={"auth-form__footer"}> 
          Уже зарегистрированы?
          <Link className={"auth-form__link"} to="/signin">Войти</Link>
        </p>
      ) : (
        <p className={"auth-form__footer"}> 
          Еще не зарегистрированы?
          <Link className={"auth-form__link"} to="/signup">Регистрация</Link>
        </p>
      )}
    </div>
  );
}

export default AuthForm;
