import React from 'react';

import styles from './Input.module.css';

function Input(props) {
  const {
    addClassName,
    title,
    className,
    errorMessage,
    name,
    type,
    minLength,
    maxLength,
    value,
    pattern,
  } = props;

  return (
    <div className={`${styles.input__container} ${styles[addClassName]}`}>
      <span className={styles.input__title}>{title}</span>
      <input
        className={`${styles[className]} ${errorMessage ? styles.input_error : ''}`}
        name={name}
        type={type}
        required
        minLength={minLength}
        maxLength={maxLength}
        value={value || ''}
        pattern={pattern}
      />
      <p className={errorMessage ? `${styles['input-error']} ${styles['input-error__profile']}` : styles['input-error']}>
        {errorMessage}
      </p>
    </div>
  );
}

export default Input;
