import React, { useState, useEffect } from 'react';
import { useLocation, Link } from 'react-router-dom';
import Navigation from "../Navigation/Navigation.jsx";
import NavTab from "../NavTab/NavTab.jsx";
import BurgerMenu from "./BurgerMenu/BurgerMenu.jsx";
import headerLogo from "../../images/logo__header.svg";
import styles from "./Header.module.css";

function Header() {
  const location = useLocation();
  const [isBurgerMenuOpen, setIsBurgerMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const toggleBurgerMenu = () => setIsBurgerMenuOpen(!isBurgerMenuOpen);

  const loggedInRoutes = ['/movies', '/profile', '/saved-movies'];
  const noHeaderRoutes = ['/signin', '/signup', '/404'];

  if (noHeaderRoutes.includes(location.pathname)) return null;

  return (
    <header className={styles.header}>
      <div className={styles.header__container}>
        <Link to="/" className={styles.header__logo}>
          <img src={headerLogo} alt="Лого" />
        </Link>
        {loggedInRoutes.includes(location.pathname) ? (
          <>
            {isMobile && <BurgerMenu isOpen={isBurgerMenuOpen} onClick={toggleBurgerMenu} />}
            {(isMobile && isBurgerMenuOpen) || (!isMobile && <Navigation />)}
          </>
        ) : <NavTab />}
      </div>
    </header>
  );
}

export default Header;
