import React, { useState } from 'react';
import styles from './MoviesCard.module.css'; 

function MoviesCard({ movieName, movieImage, movieLink, ...props  }) {
  const [isLiked, setIsLiked] = useState(false);

  const handleLikeClick = () => {
    // Только если это не страница сохранённых фильмов
    if (!props.isSavedPage) {
      setIsLiked(!isLiked);
      props.onLike(props.movie);
    }
  };

  const handleRemoveClick = () => {
    // Для страницы сохранённых фильмов
    if (props.isSavedPage) {
      props.onRemove(props.movie);
    }
  };

  return (
    <article className={styles["movies-card"]}>
      <div className={styles["movies-card__cover"]}>
        <a href={movieLink} target="_blank" rel="noreferrer">
          <img
            className={styles["movies-card__image"]}
            src={movieImage}
            alt={movieName}
          />
        </a>
      </div>
      <div className={styles["movies-card__footer"]}>
        <div className={styles["movies-card__data"]}>
          <h2 className={styles["movies-card__name"]}>{movieName}</h2>
          <button
            className={props.isSavedPage ? styles["button-card-delete"] : (isLiked ? styles["button-card-like_active"] : styles["button-card-like"])}
            type="button"
            aria-label={props.isSavedPage ? "удалить фильм" : "сохранить фильм"}
            onMouseEnter={props.isSavedPage ? handleRemoveClick : null}
            onMouseLeave={props.isSavedPage ? handleLikeClick : null}
            onClick={props.isSavedPage ? handleRemoveClick : handleLikeClick}
          />
        </div>
        <div className={styles["movies-card__meta"]}>
          <span className={styles["movie__duration"]}>1ч 42м</span>
        </div>
      </div>
    </article>
  );
}

export default MoviesCard;
