import React from 'react';
import styles from './MoviesCard.module.css'; 

function MoviesCard({ movie }) {
  const durationHours = Math.floor(movie.duration / 60);
  const durationMinutes = movie.duration - durationHours * 60;
  const durationString = durationHours
    ? `${durationHours}ч ${durationMinutes}м`
    : `${durationMinutes}мин`;

  return (
    <article className={styles["movie"]}>
      <div className={styles["movie__cover"]}>
        <a href={movie.trailerLink} target="_blank" rel="noreferrer">
          <img
            className={styles["movie__image"]}
            src={movie.image}
            alt={movie.nameRU}
          />
        </a>
      </div>
      <div className={styles["movie__footer"]}>
        <div className={styles["movie__data"]}>
          <h2 className={styles["movie__name"]}>{movie.nameRU}</h2>
          <button
            className={styles["button-card-like"]}
            type="button"
            aria-label="сохранить/удалить фильм"
          />
        </div>
        <div className={styles["movie__meta"]}>
          <span className={styles["movie__duration"]}>{durationString}</span>
        </div>
      </div>
    </article>
  );
}

export default MoviesCard;
