import React from "react";
import { useLocation } from 'react-router-dom';
import { Link } from "react-router-dom";
import BurgerMenu from "./BurgerMenu/BurgerMenu.jsx";
import Navigation from "../Navigation/Navigation.jsx";
import NavTab from "../NavTab/NavTab.jsx";
import headerLogo from "../../images/logo__header.svg";
import styles from "./Header.module.css";

function Header({ loggedIn, pathname, onBurgerClose, onClickBurger }) {
  const location = useLocation();

  // Массив путей, на которых Header не должен отображаться
  // const noHeaderPaths = ['/signin', '/signup', '/404'];

  const headerOnLanding = location === '/' ? 'header_landing-color' : null;


  if (location === '/signin' || location === '/signup' || location === '/404') return null;

  return (
    <header className={`${styles.header} ${styles[headerOnLanding]}`}>
      <div className={styles.header__container}>
        <Link to="/" className={styles.header__logo}>
          <img className={styles.logo_img} src={headerLogo} alt="Лого" />
        </Link>
        {loggedIn ? <Navigation onBurgerClose={onBurgerClose} /> : <NavTab />}
        {loggedIn && <BurgerMenu handleClickBurger={onClickBurger} />}
      </div>
    </header>
  );
}

export default Header;
