import React from 'react';
import FilterCheckBox from '../FilterCheckbox/FilterCheckbox.jsx';
import styles from './Search.module.css'; // Import CSS module

function Search({ searchHandler, searchValue, searchChangeValueHandler, formValidation }) {
  return (
    <section className={styles.search}>
      <div className={styles.search__container}>
        <form
          className={styles.search__form}
          name="search"
          autoComplete="off"
          onSubmit={searchHandler}
          noValidate
        >
          <div className={styles.search__wrapper}>
            <i className={styles.search__icon} />
            <input
              className={`${styles.search__input} ${formValidation ? '' : styles.search__inputInvalid}`}
              name="search"
              type="search"
              placeholder={formValidation ? 'Фильм' : 'Нужно ввести ключевое слово'}
              value={searchValue || ''}
              onChange={searchChangeValueHandler}
              required
            />
            <div className={styles.search__toolbox}>
              <button
                className={styles.search__button}
                type="submit"
                aria-label="search-button"
                disabled={!formValidation}
              />
              <FilterCheckBox />
            </div>
          </div>
        </form>
      </div>
    </section>
  );
}

export default Search;
