import React, { useState } from 'react';
import SearchForm from '../SearchForm/SearchForm.jsx';
import MoviesCardsList from '../MoviesCardList/MoviesCardList.jsx';
import styles from './Movies.module.css'; 

function Movies() {
  const [savedMovies, setSavedMovies] = useState([]); // Список сохраненных фильмов

  // Функция добавления фильма в список сохраненных
  const handleLike = (movie) => {
    setSavedMovies([...savedMovies, movie]);
  };

  // Функция удаления фильма из списка сохраненных
  const handleRemove = (movie) => {
    setSavedMovies(savedMovies.filter((m) => m.movieName !== movie.movieName));
  };

  return (
    <main className={styles["movies"]}>
      <SearchForm />
      <MoviesCardsList onLike={handleLike} onRemove={handleRemove} />
    </main>
  );
}

export default Movies;
